export function AnimationEditor({
	subComponentName,
	pseudoState,
	iterateProps,
	selectorPref,
	styleProps,
	selectedComponent,
	saveStyle,
	properties,
}: StyleEditorsProps) {
	const propValues = ANIMATION_PROPS.map(prop =>
		extractValue({
			subComponentName,
			prop,
			iterateProps,
			pseudoState,
			selectorPref,
			selectedComponent,
		}),
	);

	const isAdvanced = propValues.map(e => e.value).some(e => !!e.location);

	const [searchFilter, setSearchFilter] = React.useState<string>('');

	if (isAdvanced) {
		return (
			<div className="_simpleEditor _warning">
				<svg xmlns="http://www.w3.org/2000/svg" width="14" height="14" viewBox="0 0 32 32">
					<g
						id="Group_75"
						data-name="Group 75"
						transform="translate(21.016 2) rotate(90)"
					>
						<path
							id="Path_161"
							data-name="Path 161"
							d="M4.758,0c4.073.656,6.593,3.713,14.274,4.758C11.405,5.837,8.971,8.824,4.758,9.516A4.758,4.758,0,0,1,4.758,0Z"
							transform="translate(0 0.5)"
							fillOpacity={0.5}
							fill="currentColor"
						/>
						<circle
							id="Ellipse_13"
							data-name="Ellipse 13"
							cx="2.855"
							cy="2.855"
							r="2.855"
							transform="translate(22.664 2.403)"
							fill="currentColor"
						/>
					</g>
				</svg>
				Expressions are used in the properties, use advanced editor to edit.
			</div>
		);
	}

	const props = propValues.reduce((a, e) => {
		a[e.actualProp.substring(e.actualProp.indexOf('-') + 1)] = (e.value?.value ?? '')
			.split(',')
			.map((x: string) => x.trim())
			.filter((x: string) => x !== '')
			.map((x: string) => (x === '' ? undefined : x));
		return a;
	}, {} as any);

	const animations = [];

	const saveAnimation = (index: number, prop: string, value: string) => {
		const v = duplicate(props[prop]);

		while (v.length <= index) v.push(undefined);

		v[index] = value;
		const propValue = v.map((x: string) => (x ? x : ANIMATION_PROPS_DEFAULTS[prop])).join(',');

		const { screenSize, actualProp, compProp } =
			propValues.find(e => e.actualProp.endsWith(prop)) ?? {};

		if (!actualProp || !compProp || !screenSize) return;

		valueChanged({
			styleProps,
			properties,
			screenSize,
			actualProp,
			value: {
				value: propValue,
			},
			compProp,
			pseudoState,
			saveStyle,
			iterateProps,
		});
	};

	for (let i = 0; i < props.animationName.length; i++) {
		if (!props.animationName[i]) continue;
		animations.push(
			<div className="_simpleEditorGroup" key={`${props.animationName[i]}_group_${i}`}>
				<div className="_simpleEditorGroupTitle _gradient">
					<span>Customize Animation</span>
					<span className="_controls">
						<IconsSimpleEditor
							selected={''}
							onChange={v => {
								if (v === 'Delete') {
									valuesChanged({
										styleProps,
										properties,
										pseudoState,
										saveStyle,
										iterateProps,
										propValues: propValues
											.filter(e => e.value.value)
											.map(e => ({
												actualProp: e.actualProp,
												compProp: e.compProp,
												screenSize: e.screenSize,
												value: {
													value: e.value.value
														.trim()
														.split(',')
														.map((x: string) => x.trim())
														.filter(
															(_: string, ind: number) => ind !== i,
														)
														.join(','),
													location: e.value.location,
												},
											})),
									});
									return;
								}
							}}
							withBackground={false}
							options={[
								{
									name: 'Delete',
									description: 'Delete this animation',
									width: '13',
									height: '14',
									icon: (
										<>
											<path
												d="M3.93393 0.483984L3.74107 0.875H1.16964C0.695536 0.875 0.3125 1.26602 0.3125 1.75C0.3125 2.23398 0.695536 2.625 1.16964 2.625H11.4554C11.9295 2.625 12.3125 2.23398 12.3125 1.75C12.3125 1.26602 11.9295 0.875 11.4554 0.875H8.88393L8.69107 0.483984C8.54643 0.185938 8.24911 0 7.925 0H4.7C4.37589 0 4.07857 0.185938 3.93393 0.483984ZM11.4554 3.5H1.16964L1.7375 12.7695C1.78036 13.4613 2.34286 14 3.02054 14H9.60446C10.2821 14 10.8446 13.4613 10.8875 12.7695L11.4554 3.5Z"
												strokeWidth="0"
											/>
										</>
									),
								},
							]}
						/>
					</span>
				</div>
				<div className="_simpleEditorGroupContent">
					<div className="_editorLine">
						<span className="_label">Animation </span>
						<Dropdown
							value={props.animationName[i]}
							onChange={e => saveAnimation(i, 'animationName', e as string)}
							placeholder="Animation Name"
							options={ANIMATIONS_LIST}
							showNoneLabel={false}
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Duration </span>
						<TimeSize
							value={props.animationDuration[i]}
							onChange={e => saveAnimation(i, 'animationDuration', e)}
							placeholder="Duration"
							autofocus={true}
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Timing Function </span>
						<Dropdown
							value={props.animationTimingFunction[i]}
							onChange={e => saveAnimation(i, 'animationTimingFunction', e as string)}
							placeholder="Timing Function"
							options={[
								{ name: 'ease', displayName: 'Ease' },
								{ name: 'ease-in', displayName: 'Ease In' },
								{ name: 'ease-out', displayName: 'Ease Out' },
								{ name: 'ease-in-out', displayName: 'Ease In Out' },
								{ name: 'linear', displayName: 'Linear' },
								{ name: 'step-start', displayName: 'Step Start' },
								{ name: 'step-end', displayName: 'Step End' },
							]}
							showNoneLabel={false}
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Delay </span>
						<TimeSize
							value={props.animationDelay[i]}
							onChange={e => saveAnimation(i, 'animationDelay', e)}
							placeholder="Delay"
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Iteration Count </span>
						<Dropdown
							value={props.animationIterationCount[i]}
							onChange={e => saveAnimation(i, 'animationIterationCount', e as string)}
							placeholder="Iteration Count"
							options={[
								{ name: '1', displayName: '1' },
								{ name: '2', displayName: '2' },
								{ name: '3', displayName: '3' },
								{ name: '4', displayName: '4' },
								{ name: '5', displayName: '5' },
								{ name: '6', displayName: '6' },
								{ name: '7', displayName: '7' },
								{ name: '8', displayName: '8' },
								{ name: '9', displayName: '9' },
								{ name: '10', displayName: '10' },
								{ name: '11', displayName: '11' },
								{ name: '12', displayName: '12' },
								{ name: '13', displayName: '13' },
								{ name: '14', displayName: '14' },
								{ name: '15', displayName: '15' },
								{ name: 'infinite', displayName: 'Infinite' },
							]}
							showNoneLabel={false}
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Direction </span>
						<Dropdown
							value={props.animationDirection[i]}
							onChange={e => saveAnimation(i, 'animationDirection', e as string)}
							placeholder="Direction"
							options={[
								{ name: 'normal', displayName: 'Normal' },
								{ name: 'reverse', displayName: 'Reverse' },
								{ name: 'alternate', displayName: 'Alternate' },
								{ name: 'alternate-reverse', displayName: 'Alternate Reverse' },
							]}
							showNoneLabel={false}
						/>
					</div>
					<div className="_editorLine">
						<span className="_label">Fill Mode </span>
						<Dropdown
							value={props.animationFillMode[i]}
							onChange={e => saveAnimation(i, 'animationFillMode', e as string)}
							placeholder="Fill Mode"
							options={[
								{ name: 'none', displayName: 'None' },
								{ name: 'forwards', displayName: 'Forwards' },
								{ name: 'backwards', displayName: 'Backwards' },
								{ name: 'both', displayName: 'Both' },
							]}
							showNoneLabel={false}
						/>
					</div>
				</div>
			</div>,
		);
	}

	return (
		<>
			<svg
				width="0"
				height="0"
				viewBox="0 0 37 38"
				fill="none"
				xmlns="http://www.w3.org/2000/svg"
			>
				<defs>
					<linearGradient
						id="no_color_gradient"
						x1="7.50021"
						y1="2.00809"
						x2="22.5021"
						y2="27.9922"
						gradientUnits="userSpaceOnUse"
					>
						<stop stopColor="#C7C7D1" />
						<stop offset="1" stopColor="#E8E9ED" />
					</linearGradient>
					<linearGradient
						id={`color_gradient`}
						x1="7.50021"
						y1="2.00809"
						x2="22.5021"
						y2="27.9922"
						gradientUnits="userSpaceOnUse"
					>
						<stop stopColor="#43B2FF" />
						<stop offset="1" stopColor="#52BD94" />
					</linearGradient>
				</defs>
			</svg>
			<div className="_simpleEditorBigSelector">
				<div className="_searchBox">
					<input
						placeholder="Search animation style"
						value={searchFilter ?? ''}
						onChange={e => setSearchFilter(e.target.value)}
					/>
					<svg
						width="14"
						height="14"
						viewBox="0 0 14 14"
						fill="none"
						xmlns="http://www.w3.org/2000/svg"
					>
						<path
							d="M12.2499 1.75173C9.91393 -0.583909 6.11766 -0.583909 3.78172 1.75173C1.69596 3.83722 1.47778 7.09199 3.12428 9.42762L0.299629 12.2519C0.107634 12.4439 0 12.7027 0 12.9762C0 13.2496 0.107634 13.5055 0.299629 13.7004C0.491624 13.8953 0.750526 14 1.02397 14C1.29742 14 1.55341 13.8924 1.74832 13.7004L4.57297 10.8761C5.60276 11.6033 6.81 11.9727 8.02015 11.9727C9.55321 11.9727 11.0863 11.388 12.2528 10.2217C14.5829 7.88314 14.5829 4.08736 12.2499 1.75173ZM11.2172 9.1862C9.45139 10.9517 6.57728 10.9517 4.81151 9.1862C3.95626 8.33107 3.485 7.19379 3.485 5.98379C3.485 4.7738 3.95626 3.63652 4.81151 2.78138C5.69585 1.89716 6.85364 1.45795 8.01434 1.45795C9.17503 1.45795 10.3328 1.90007 11.2172 2.78138C12.9829 4.54984 12.9829 7.42066 11.2172 9.1862Z"
							fill="black"
							fillOpacity="0.15"
						/>
					</svg>
				</div>
				<div className="_searchResult">
					{(searchFilter.trim()
						? ANIMATIONS_LIST.filter(e =>
								e.displayName
									.toLowerCase()
									.includes(searchFilter.toLowerCase().trim()),
						  )
						: ANIMATIONS_LIST
					)
						.filter(e => e.name !== '_waitOpacity')
						.map(e => {
							return (
								<div
									className="_searchResultItem"
									key={e.name}
									onClick={() => {
										const { screenSize, actualProp, compProp } = extractValue({
											subComponentName,
											prop: 'animationName',
											iterateProps,
											pseudoState,
											selectorPref,
											selectedComponent,
										});

										valueChanged({
											styleProps,
											properties,
											screenSize,
											actualProp,
											value: {
												value: (props.animationName.length
													? [...props.animationName, e.name]
													: [e.name]
												).join(','),
											},
											compProp,
											pseudoState,
											saveStyle,
											iterateProps,
										});
									}}
								>
									<div className="_animationIcon">
										<div className="_default">
											{React.cloneElement(
												ICONS.find(x => e.name.startsWith(x.name))?.icon ??
													OTHER,
											)}
										</div>
										<div
											className="_hovered"
											style={{ animation: `${e.name} 2s linear infinite` }}
										>
											{e.name.startsWith('_swing') ||
											e.name.startsWith('_spin') ? (
												<svg
													width="38"
													height="38"
													viewBox="0 0 38 38"
													fill="none"
													xmlns="http://www.w3.org/2000/svg"
												>
													<rect
														x="2"
														y="12"
														width="36"
														height="18"
														rx="2"
														fill="url(#color_gradient)"
													/>
												</svg>
											) : (
												<svg
													width="30"
													height="30"
													viewBox="0 0 30 30"
													fill="none"
													xmlns="http://www.w3.org/2000/svg"
												>
													<path
														d="M15 30C23.2843 30 30 23.2843 30 15C30 6.71574 23.2843 0 15 0C6.71573 0 0 6.71574 0 15C0 23.2843 6.71573 30 15 30Z"
														fill={`url(#color_gradient)`}
													/>
												</svg>
											)}
										</div>
									</div>
									<div className="_animationName">
										{e.displayName.replace(' ', '\n')}
									</div>
								</div>
							);
						})}
				</div>
			</div>
			{animations}
		</>
	);
}
